export class EmployeeDashboardComponent {
    //------------------------------GLOBAL VARIABLES END HERE----------------------//
    constructor(fb, ApiService, fireStoreDb, storage, ngxService) {
        this.fb = fb;
        this.ApiService = ApiService;
        this.fireStoreDb = fireStoreDb;
        this.storage = storage;
        this.ngxService = ngxService;
        //--------------GLOBAL VARIABLES DECLARED HERE---------------------------------//
        this.employeeModelObject = new employeeData;
        this.buttonText = 'Submit';
        this.myLocalArray = [];
        this.formattedArray = {};
        this.sortingOrder = 'ascending';
        // ---------------------------------------------//Edit Employee Method//---------------------------------------------//
        this.editForm = new FormGroup({
            name: new FormControl('', Validators.required),
            phone: new FormControl('', Validators.required),
            email: new FormControl('', [Validators.required, Validators.pattern("^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$")]),
            address: new FormControl('', [Validators.required]),
            picture: new FormControl('', Validators.required)
        });
        //----------------------------NEW EMPLOYEE FORM START HERE--------------------//
        this.employeeForm = new FormGroup({
            name: new FormControl('', Validators.required),
            phone: new FormControl('', Validators.required),
            email: new FormControl('', [Validators.required, Validators.pattern("^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$")]),
            address: new FormControl('', Validators.required),
            picture: new FormControl('', Validators.required)
        });
        //----------------------------NEW EMPLOYEE FORM END HERE--------------------//
    }
    ngxServiceLoader() {
        this.ngxService.start();
        setTimeout(() => {
            this.ngxService.stop();
        }, 2000);
    }
    ngOnInit() {
        this.ngxServiceLoader();
        this.getAllData(); //get data from firestore API call
    }
    get f() {
        return this.employeeForm.controls;
    }
    addFile(event) {
        this.selectedFile = event.target.files[0];
        this.ngxServiceLoader();
    }
    // ------------------------UPLOAD IMAGE + DATA TO FIREBASE STORAGE START HERE------------------------------------------//
    async uploadDataoFireStorage() {
        var uploadedImage = Date.now();
        const filePath = `employeeImages/${uploadedImage}`;
        const snap = await this.storage.upload(filePath, this.selectedFile);
        this.geturl(snap);
    }
    async geturl(snap) {
        const url = await snap.ref.getDownloadURL();
        this.url = url;
        if (url) {
            let userObject = this.employeeForm.value;
            userObject['picture'] = url;
            const id = this.fireStoreDb.createId();
            userObject['id'] = id;
            this.ApiService.addUser(userObject);
            this.employeeForm.reset();
            this.ngxServiceLoader();
        }
    }
    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    // ----------------------------------------------------//Delete Method//-----------------------------------------------//
    deleteEmployee(deleteIndexId) {
        this.ApiService.deleteUser(deleteIndexId);
        this.ngxServiceLoader();
    }
    // ----------------------------------------------------//GET Method//-----------------------------------------------//
    getAllData() {
        this.ApiService.getEmployees().subscribe((jsonData) => {
            this.myLocalArray = jsonData;
        });
    }
    onEdit(id) {
        const mySelectedIndexFilter = this.myLocalArray.filter((data) => data.id === id);
        this.editForm.setValue({
            name: mySelectedIndexFilter[0].name,
            phone: mySelectedIndexFilter[0].phone,
            email: mySelectedIndexFilter[0].email,
            address: mySelectedIndexFilter[0].address,
            picture: mySelectedIndexFilter[0].picture
        });
        this.base64Image = mySelectedIndexFilter[0].picture;
        this.editableId = id;
    }
    //----------------------------------------------------//Update Method//------------------------------------------------//
    async onUpdate() {
        this.ngxServiceLoader();
        var uploadedImage = Date.now();
        const filePath = `employeeImages/${uploadedImage}`;
        const snap = await this.storage.upload(filePath, this.selectedFile);
        this.getUrlForImage(snap);
    }
    async getUrlForImage(snap) {
        const url = await snap.ref.getDownloadURL();
        if (url) {
            const userObject = this.editForm.value;
            debugger;
            userObject.id = this.editableId;
            userObject['picture'] = url; // it will upload my picture on same index
            this.ApiService.updateUser(userObject, this.editableId);
            this.editForm.reset();
        }
    }
    //--------------------------------------SELECT FILE FOR UPLOAD----------------------------------------------------//
    selectFile(event) {
        let uploadedFile = event.target.files[0];
        let reader = new FileReader();
        reader.readAsText(uploadedFile);
        reader.onload = (e) => {
            let csv = reader.result;
            this.convertCsvToArray(csv);
        };
    }
    //---------------------------------------------Convert to Array-------------------------------------------//
    convertCsvToArray(csvString) {
        var arrayLines = csvString.split('\r\n');
        var headerValues = arrayLines[0].split(/,/);
        var dataValues = arrayLines.splice(1).map(function (dataLine) { return dataLine.split(/,/); });
        let formattedArray = dataValues.map(function (rowValues) {
            var row = {};
            headerValues.forEach(function (headerValues, index) {
                row[headerValues] = (index <= rowValues.length) ? rowValues[index] : null;
            });
            return row;
        });
        var nonEmptyArray = formattedArray.filter((nonEmptyArrayValue) => {
            if (nonEmptyArrayValue.name && nonEmptyArrayValue.email)
                return nonEmptyArrayValue;
        });
        this.formattedArray = nonEmptyArray;
    }
    uploadFileBtn() {
        this.ngxServiceLoader();
        this.formattedArray.every((element) => {
            const id = this.fireStoreDb.createId();
            element['id'] = id;
            this.ApiService.addUser(element);
            return true;
        });
    }
    //----------------------------------------Export CSV Method here--------------------------------------------//
    exportCSV() {
        var options = {
            fieldSeparator: ',',
            quoteStrings: '"',
            decimalseparator: '.',
            showLabels: true,
            showTitle: true,
            title: 'Company Employee Details',
            useBom: true,
            noDownload: false,
            headers: ["Name", "Email", "Phone", "Address", "Id", "Picture"]
        };
        new ngxCsv(this.myLocalArray, "EmployeeReport", options);
    }
    //--------------RESET METHOD-----------------//
    resetForm() {
        this.employeeForm.reset();
    }
    //--------------SORTING METHOD-----------------//
    sortArray(property, order) {
        this.ngxServiceLoader();
        if (order == 'ascending') {
            this.myLocalArray.sort((a, b) => b[property] > a[property] ? -1 : 1);
            this.sortingOrder = 'descending';
        }
        else {
            this.myLocalArray.sort((a, b) => b[property] > a[property] ? 1 : -1);
            this.sortingOrder = 'ascending';
        }
    }
}
